import React from "react";
import { useSelector } from "react-redux";

export default function Intro() {
  const { loading, portfolioData } = useSelector((state) => state.root);
  return (
    <div className="h-[80vh] bg-primary flex flex-col items-start justify-center gap-8 py-10">
      <h1 className="text-white">Hi, We are</h1>
      <h1 className="text-7xl sm:text-3xl text-secondary font-semibold">
        HyperText Savvies
      </h1>
      <h1 className="text-7xl sm:text-3xl text-white font-semibold">
        We are one of the best WebTech firm in the world.
      </h1>
      <p className="text-white w-2/3">
        HTS is a web development company that specializes in building dynamic
        and user-friendly websites using the MERN stack. MERN is a popular
        JavaScript-based technology stack that allows developers to build
        full-stack web applications using a single programming language.
      </p>
      <button className="border-2 border-tertiary text-tertiary px-10 py-3 rounded">
        Get Started
      </button>
    </div>
  );
}
